    const meta = {
      name: inputVideoFile.name,
      duration: el.duration,
      videoWidth: el.videoWidth,
      videoHeight: el.videoHeight
    };
    console.log({ meta });
    setVideoMeta(meta);
    const thumbNails = await getThumbnails(meta);
    console.log(thumbNails)
    setThumbNails(thumbNails);
