// src/App.jsx or src/App.tsx
import React, { useState } from 'react';
import ReactPlayer from 'react-player';
import { VideoToFrames,VideoToFramesMethod } from './VideoToFrame.ts';

function Video() {
  const [videoURL, setVideoURL] = useState(null);
  const [images, setImages] = useState([]);

  const handleVideoChange = async (event) => {
    const file = event.target.files?.[0];

    if (file) {
      const videoURL = URL.createObjectURL(file);
      setVideoURL(videoURL);
      const frames = await VideoToFrames.getFrames(
        videoURL,
        30,
        VideoToFramesMethod.totalFrames
      );

     setImages(frames);
    }
  };
  const now = new Date().toDateString();

  return (
    <div className="App min-h-screen flex flex-col items-center bg-gray-100">
      <div className="max-w-md w-full mt-6 p-6 bg-white rounded-md shadow-md">
        <input
          type="file"
          accept="video/*"
          onChange={handleVideoChange}
          className="my-2"
        />
      </div>
      {videoURL && (
          <div className="custom-player-container mt-10 flex justify-center p-6 rounded-xl shadow-lg bg-white w-1/2">
            <ReactPlayer
              url={videoURL}
              controls
              playing
              width="100%"
              height="100%"        
            />
          </div>
        )}

{images?.length > 0 && (
        <div className="output flex overflow-x-scroll mt-5 mx-44">
          {images.map((imageUrl, index) => (
              <img src={imageUrl} alt="" className='w-44' />
          ))}
        </div>
      )}
    </div>
  );
}

export default Video;
