// src/App.jsx or src/App.tsx
import React, { useState } from 'react';
import ReactPlayer from 'react-player';

function Video() {
  const [videoURL, setVideoURL] = useState(null);

  const handleVideoChange = (event) => {
    const file = event.target.files?.[0];

    if (file) {
      const videoURL = URL.createObjectURL(file);
      setVideoURL(videoURL);
    }
  };

  return (
    <div className="App min-h-screen flex flex-col items-center bg-gray-100">
      <div className="max-w-md w-full mt-6 p-6 bg-white rounded-md shadow-md">
        <input
          type="file"
          accept="video/*"
          onChange={handleVideoChange}
          className="my-2"
        />
      </div>
      {videoURL && (
          <div className="custom-player-container mt-10 flex justify-center shadow-sm">
            <ReactPlayer
              url={videoURL}
              controls
              playing
              width="50%"
              height="50%"
              className=""
            />
          </div>
        )}
    </div>
  );
}

export default Video;
