// src/App.jsx or src/App.tsx
import React, { useState , useRef} from 'react';
import ReactPlayer from 'react-player';
import { VideoToFrames,VideoToFramesMethod } from './VideoToFrame.ts';


function Video() {
  const [videoURL, setVideoURL] = useState(null);
  const [images, setImages] = useState([]);
  const [loading,setLoading] = useState(false) 
  const fileInputRef = useRef(null);

  const handleVideoChange = async (event) => {
    const file = event.target.files?.[0];
    if (file) {
        setLoading(true)
      const videoURL = URL.createObjectURL(file);
      setVideoURL(videoURL);
      const frames = await VideoToFrames.getFrames(
        videoURL,
        15,
        VideoToFramesMethod.totalFrames
      );

      setLoading(false)
     setImages(frames);
    }
  };

  const handleDrop = async (event) => {
    event.preventDefault();
    setDragging(false);

    const file = event.dataTransfer.files?.[0];
    if (file) {
      setLoading(true);

      const videoURL = URL.createObjectURL(file);
      setVideoURL(videoURL);
      const frames = await VideoToFrames.getFrames(
        videoURL,
        15,
        VideoToFramesMethod.totalFrames
      );

      setLoading(false);
      setImages(frames);
    }
  };
  const now = new Date().toDateString();

  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  return (

    <div className="App min-h-screen flex flex-col items-center justify-center bg-gray-100">
        {loading && <div className='absolute w-screen h-screen flex justify-center items-center left-0 top-0 bg-black opacity-50'>
            <div className="loader"></div>
        </div>}
      <div className="max-w-md flex flex-col w-full mt-10 p-20 bg-white rounded-lg bg-damier shadow-md" onDrop={handleDrop}>
        <input
          type="file"
          accept="video/*,.mkv"
          onChange={handleVideoChange}
          className="my-2 hidden"
          ref={fileInputRef}  
        />

        <button className='px-5 py-4 bg-purple-800 text-white rounded-md' onClick={handleButtonClick}> Choose video</button>
        <div className='text-center text-slate-800 font-semibold mt-5'>Or drop a video here</div>
      </div>
      {videoURL && (
          <div className="custom-player-container mt-10 flex justify-center p-6 rounded-xl shadow-lg bg-white w-1/2">
            <ReactPlayer
              url={videoURL}
              controls
              width="100%"
              height="100%"       
            />
          </div>
        )}

{images?.length > 0 && (
        <div className="output flex overflow-x-scroll mt-5 mx-44 space-x-3">
          {images.map((imageUrl, index) => (
              <img src={imageUrl} alt="" className='w-44 rounded-md' />
          ))}
        </div>
      )}
    </div>
  );
}

export default Video;
